/*
 * 延迟加载图片
 */

/**
 * @author cmli@ctrip.com
 * @class cLazyload
 *
 */
define(['libs'], function(libs) {
  var L = {};
  L.lazyload = function(img, onStart, onError, onComplete) {
    var $img = $(img);

    var noImg = 'data:image/png;base64,';

    var loadImg = 'data:image/png;base64,';

    var errorImg = 'data:image/png;base64,';

    $img.each(function() {
      var el = $(this),
        src = el.attr('src'),
        newImg = $(new Image());
      // 修复图片版本号导致的不能显示问题 shbzhang 2013/12/12
      //if (!/\.(gif|jpg|jpeg|png|GIF|JPG|PNG)$/i.test(src)) src = '';

      el.addClass('cui-item-imgbg');
      if (typeof src == 'string' && src.length > 0) {
        el.attr('src', loadImg);
        newImg.bind('error', function() {
          el.attr('src', errorImg);
        });
        newImg.bind('load', function() {
          el.attr('src', src);
          el.removeClass('cui-item-imgbg');
        });
        newImg.attr('src', src);
      } else {
        el.attr('src', noImg);

      }
    });
  };
  return L;
});